import { Cell } from "../Cell";
import { Colors } from "../Colors";
import { Figure, FigureNames } from "../Figure";

import black_bishop from '../icons/black_bishop.png';
import white_bishop from '../icons/white_bishop.png';

export class Bishop extends Figure {

    constructor(color: Colors, cell: Cell) {
        super(color, cell)
        this.name = FigureNames.BISHOP
        this.figureImg = this.color === Colors.WHITE ? white_bishop : black_bishop
    }

    canMove(cell: Cell): boolean {
        //Общее правило для всех фигур
        if (!super.canMove(cell)) {
            return false
        }
        /*
        let firstDiagonal = this.cell.board.getDiagonal(this.cell.id, 9)
        let secondDiagonal = this.cell.board.getDiagonal(this.cell.id, 7)
        if (firstDiagonal.find(cage => cage.id === cell.id)) return true
        if (secondDiagonal.find(cage => cage.id === cell.id)) return true
        */

        if (Math.abs(this.cell.x - cell.x) === Math.abs(this.cell.y - cell.x)) return true
        return false
    }

}
